<script>

    if (__sysinfo.rightNews){
        $("#right-notice").html(__sysinfo.rightNews);
        $("#right-notice-box").attr("style","");
    }else {
        $("#right-notice-box").attr("style","display: none");
    }

    $("#noticeLink").html(__sysinfo.newNews);//

    var content;
    if (__sysinfo.paytype == 1){

        content =
            "<em>|</em>" +
            "<span class=\"nameType\"></span>余额：<span id=\"usableCreditSpan\">load...</span>\n" +
            "<em>|</em>" ;

    }else {
        content = "<span class=\"nameType\"></span>额度：<span id=\"creditSpan\">load...</span>\n" +
            "<em>|</em>\n" +
            "<span class=\"nameType\"></span>可用：<span id=\"usableCreditSpan\">load...</span>";
    }
    $("#ppurl").html(content);


    $("#noticeLink").unbind("click").click(function () {
        G.mask();
        G.ajax("newinfos",function (json) {
            G.maskClose();

            if (json.length == 0) return;

            var newsWrap = "";
            newsWrap += "<table class='order' ><thead><tr><th width='450'>消息内容</th></tr></thead>";
            newsWrap += "<tbody>";
            for (var i = 0; i < json.length; i++){
                newsWrap += "<tr><td style='word-wrap: break-word;word-break:break-all;text-align: left;padding-left: 10px'>"+ json[i][0] +"</td></tr>";

            }
            newsWrap += "</tbody>";
            var content = G.overflowDiv({ id: "newsWrap", content: newsWrap });
            G.alert({ content: content, title: "消息", width: 450});

        },function () {
            G.maskClose();
        });

    });


</script>
